package qna.domain;

import qna.CannotDeleteException;
import qna.NotFoundException;
import qna.UnAuthenticationException;
import qna.UnAuthorizedException;
import qna.domain.common.BaseEntity;

import javax.persistence.*;
import java.util.Objects;

@Entity
public class Answer extends BaseEntity {
    public static final String CANNOT_DELETE_ANSWER = "답변을 삭제할 수 없습니다 ,";
    /**
     * create table answer
     * (
     * id          bigint generated by default as identity,
     * contents    clob,
     * created_at  timestamp not null,
     * deleted     boolean   not null,
     * question_id bigint,
     * updated_at  timestamp,
     * writer_id   bigint,
     * primary key (id)
     * )
     */
    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Long id;

    @Embedded
    private Contents contents;

    @Embedded
    private Deletion deleted;

    @ManyToOne(cascade = CascadeType.ALL)
    private Question question;

    @ManyToOne(cascade = CascadeType.ALL)
    @JoinColumn(name = "writer_id", foreignKey = @ForeignKey(name = "fk_answer_writer"))
    private User writer;

    public Answer(User writer, Question question, String contents) {
        this(null, writer, question, contents);
    }

    public Answer(Long id, User writer, Question question, String contents) {
        this.id = id;

        if (Objects.isNull(writer)) {
            throw new UnAuthorizedException();
        }

        if (Objects.isNull(question)) {
            throw new NotFoundException();
        }

        this.writer = writer;
        this.question = question;
        this.contents = new Contents(contents);
        this.deleted = new Deletion();
        this.question.addAnswer(this);
    }

    public Answer() {

    }

    public Long getId() {
        return id;
    }

    public Long getQuestionId() {
        return question.getId();
    }

    public User writer(){
        return this.writer;
    }
    public boolean isDeleted() {
        return this.deleted.isDeleted();
    }

    public DeleteHistory delete(User user) throws CannotDeleteException {
        try {
            this.writer.isOwner(user);
        }catch (UnAuthenticationException e){
            throw new CannotDeleteException(CANNOT_DELETE_ANSWER +e);
        }
        return this.deleted.delete(this);
    }

    @Override
    public String toString() {
        return "Answer{" +
                "id=" + id +
                ", writer=" + writer +
                ", question=" + question +
                ", contents='" + contents + '\'' +
                ", deleted=" + deleted +
                '}';
    }
}
