package qna.domain;

import qna.CannotDeleteException;
import qna.domain.common.BaseEntity;

import javax.persistence.*;

@Entity
public class Question extends BaseEntity {
    private static final String CANNOT_DELETE_QUESTION = "질문을 삭제할 수 없습니다.";
    /**
     * create table question
     * (
     * id         bigint generated by default as identity,
     * contents   clob,
     * created_at timestamp    not null,
     * deleted    boolean      not null,
     * title      varchar(100) not null,
     * updated_at timestamp,
     * writer_id  bigint,
     * primary key (id)
     * )
     */
    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Long id;

    private Contents contents;

    @Embedded
    private Deletion deleted;

    @Embedded
    private Title title;

    @ManyToOne(cascade = CascadeType.ALL)
    @JoinColumn(name = "writer_id", foreignKey = @ForeignKey(name = "fk_question_writer"))
    private User writer;

    @Embedded
    private Answers answers;

    public Question(String title, String contents) {
        this(null, title, contents);
    }

    public Question(Long id, String title, String contents) {
        this.id = id;
        this.title = new Title(title);
        this.contents = new Contents(contents);
        this.answers = new Answers();
        this.deleted = new Deletion();
    }

    public Question() {

    }

    public Question writeBy(User writer) {
        this.writer = writer;
        return this;
    }

    public Answers answers(){
        return this.answers;
    }
    public User writer() {
        return this.writer;
    }

    public void addAnswer(Answer answer) {
        answers.add(answer);
    }

    public Long getId() {
        return id;
    }

    public boolean isDeleted() {
        return this.deleted.isDeleted();
    }

    public Boolean isOwner(User user){
        return this.writer.equals(user);
    }

    public void delete(User user) throws CannotDeleteException {
        if(!isOwner(user)){
            throw new CannotDeleteException(CANNOT_DELETE_QUESTION);
        }
        this.deleted.delete();
        answers.delete(user);
    }

    @Override
    public String toString() {
        return "Question{" +
                "id=" + id +
                ", title='" + title + '\'' +
                ", contents='" + contents + '\'' +
                ", writer=" + writer +
                ", deleted=" + deleted +
                '}';
    }
}
